module BstSet = struct type 'a t = Leaf | Node of 'a * 'a t * 'a t
let empty = Leaf
let rec size = function | Leaf -> 0 | Node (_, left, right) -> 1 + size left + size right ;;
let rec add x = function | Leaf -> Node (x, Leaf, Leaf) | Node (v, l, r) when v > x -> Node (v, add x l, r) | Node (v, l, r) when v < x -> Node (v, l, add x r) | t -> t ;;
let rec mem x = function | Leaf -> false | Node (v, l, _) when v > x -> mem x l | Node (v, _, r) when v < x -> mem x r | _ -> true ;;
let rec remove_union lt = function | Leaf -> lt | Node (v, l, r) -> Node (v, remove_union lt l, r) ;;
let rec remove x = function | Leaf -> failwith "tree doesn't contain the element to be removed" | Node (v, l, r) when v > x -> Node (v, remove x l, r) | Node (v, l, r) when v < x -> Node (v, l, remove x r) | Node (_, l, r) -> remove_union l r ;;
let update x x' t = t |> remove x |> add x' end

module RBTree = struct type color = | Red | Black
type 'a tree = | Leaf | Node of (color * 'a * 'a tree * 'a tree)
let empty = Leaf
let rec mem x = function | Leaf -> false | Node (_, v, l, _) when v > x -> mem x l | Node (_, v, _, r) when v < x -> mem x r | _ -> true ;;
let balance = function | Black, z, Node (Red, y, Node (Red, x, a, b), c), d | Black, z, Node (Red, x, a, Node (Red, y, b, c)), d | Black, x, a, Node (Red, z, Node (Red, y, b, c), d) | Black, x, a, Node (Red, y, b, Node (Red, z, c, d)) -> Node (Red, y, Node (Black, x, a, b), Node (Black, z, c, d)) | a, b, c, d -> Node (a, b, c, d) ;;
let rec insert_aux x = function | Leaf -> Node (Red, x, Leaf, Leaf) | Node (c, v, l, r) when c > x -> balance (c, v, insert_aux x l, r) | Node (c, v, l, r) when c < x -> balance (c, v, l, insert_aux x r) | node -> node ;;
let insert x t = match insert_aux x t with | Leaf -> failwith "error" | Node (_, v, l, r) -> Node (Black, v, l, r) ;; end
